mapper.select:
select

from tbl_article_type


    and id = #{id}


    and name like concat('%',#{name},'%')


    and user_name like concat('%',#{userName},'%')


    and language = #{language}


      and create_at >= str_to_date(#{createAtStart},'%Y-%m-%d %T')  


      and create_at <= str_to_date(#{createAtEnd},'%Y-%m-%d %T')   



  order by ${orderBy} ${sortBy}


  limit #{start},#{ends}

select count(id)
from tbl_article_type


    and id = #{id}


    and language =#{language}
